"use strict";(self.webpackChunkdip_docs=self.webpackChunkdip_docs||[]).push([[849],{6164:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"category","label":"Introduction","collapsible":true,"collapsed":false,"items":[{"type":"link","label":"What is The DIP","href":"/docs/intro/what-is-the-dip","docId":"intro/what-is-the-dip","unlisted":false},{"type":"link","label":"Roadmap","href":"/docs/intro/roadmap","docId":"intro/roadmap","unlisted":false},{"type":"link","label":"Philosophy","href":"/docs/intro/philosophy","docId":"intro/philosophy","unlisted":false}]},{"type":"category","label":"Ethereum Storage Protocol","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"ESP Overview","href":"/docs/esp/esp-overview","docId":"esp/esp-overview","unlisted":false},{"type":"link","label":"DataPoints","href":"/docs/esp/datapoints","docId":"esp/datapoints","unlisted":false},{"type":"link","label":"ESP Storage","href":"/docs/esp/esp-storage","docId":"esp/esp-storage","unlisted":false},{"type":"link","label":"Registry","href":"/docs/esp/registry","docId":"esp/registry","unlisted":false},{"type":"link","label":"Royalties","href":"/docs/esp/royalties","docId":"esp/royalties","unlisted":false}]},{"type":"category","label":"Web Three Transfer Protocol","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"WTTP Overview","href":"/docs/wttp/wttp-overview","docId":"wttp/wttp-overview","unlisted":false},{"type":"link","label":"WTTP Methods","href":"/docs/wttp/wttp-methods","docId":"wttp/wttp-methods","unlisted":false},{"type":"link","label":"WTTP Permissions","href":"/docs/wttp/wttp-permissions","docId":"wttp/wttp-permissions","unlisted":false},{"type":"link","label":"WTTP Storage","href":"/docs/wttp/wttp-storage","docId":"wttp/wttp-storage","unlisted":false},{"type":"link","label":"WTTP Sites","href":"/docs/wttp/wttp-sites","docId":"wttp/wttp-sites","unlisted":false},{"type":"link","label":"WTTP Gateway","href":"/docs/wttp/wttp-gateway","docId":"wttp/wttp-gateway","unlisted":false},{"type":"link","label":"WTTP Deployment","href":"/docs/wttp/wttp-deployment","docId":"wttp/wttp-deployment","unlisted":false}]},{"type":"category","label":"DIP/WTTP Handler","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Handler Overview","href":"/docs/handler/handler-overview","docId":"handler/handler-overview","unlisted":false},{"type":"link","label":"Handler Installation","href":"/docs/handler/handler-installation","docId":"handler/handler-installation","unlisted":false},{"type":"link","label":"Handler Examples","href":"/docs/handler/handler-examples","docId":"handler/handler-examples","unlisted":false},{"type":"link","label":"Handler Roadmap","href":"/docs/handler/handler-roadmap","docId":"handler/handler-roadmap","unlisted":false}]},{"type":"category","label":"DIP/WTTP Browser","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Browser Overview","href":"/docs/browser/browser-overview","docId":"browser/browser-overview","unlisted":false},{"type":"link","label":"Browser Modifications","href":"/docs/browser/browser-modifications","docId":"browser/browser-modifications","unlisted":false},{"type":"link","label":"Browser Installation","href":"/docs/browser/browser-installation","docId":"browser/browser-installation","unlisted":false},{"type":"link","label":"Browser Usage","href":"/docs/browser/browser-usage","docId":"browser/browser-usage","unlisted":false}]},{"type":"category","label":"Bridge (Coming Soon)","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Bridge Overview","href":"/docs/bridge/bridge-overview","docId":"bridge/bridge-overview","unlisted":false},{"type":"link","label":"Bridge Setup","href":"/docs/bridge/bridge-setup","docId":"bridge/bridge-setup","unlisted":false},{"type":"link","label":"Bridge DNS Mapping","href":"/docs/bridge/bridge-dns-mapping","docId":"bridge/bridge-dns-mapping","unlisted":false},{"type":"link","label":"Premium DNS Management","href":"/docs/bridge/bridge-premium-dns","docId":"bridge/bridge-premium-dns","unlisted":false},{"type":"link","label":"wURL Contract Access","href":"/docs/bridge/bridge-wurl-access","docId":"bridge/bridge-wurl-access","unlisted":false}]}]},"docs":{"bridge/bridge-dns-mapping":{"id":"bridge/bridge-dns-mapping","title":"Bridge DNS Mapping","description":"DNS mapping allows you to configure how your domain routes to different decentralized resources through TXT record configuration. This provides flexible routing without requiring complex server-side configuration.","sidebar":"tutorialSidebar"},"bridge/bridge-overview":{"id":"bridge/bridge-overview","title":"Bridge Overview","description":"The DIP Bridge provides seamless HTTPS access to decentralized resources, enabling users to access Web3 content through traditional web browsers and standard HTTP protocols. Currently available at wttp.page, the bridge acts as a gateway that translates HTTP requests into decentralized data retrieval, making Web3 resources accessible through familiar web infrastructure.","sidebar":"tutorialSidebar"},"bridge/bridge-premium-dns":{"id":"bridge/bridge-premium-dns","title":"Premium DNS Management","description":"Premium DNS Management provides full control over your domain\'s DNS configuration, eliminating the need for manual TXT record management. This service offers native Web3 record management and comprehensive domain services.","sidebar":"tutorialSidebar"},"bridge/bridge-setup":{"id":"bridge/bridge-setup","title":"Bridge Setup","description":"This guide walks you through setting up the DIP Bridge for your domain, enabling HTTPS access to decentralized resources.","sidebar":"tutorialSidebar"},"bridge/bridge-wurl-access":{"id":"bridge/bridge-wurl-access","title":"wURL Contract Access","description":"wURL (Web3 URL) provides direct access to smart contracts through the bridge without requiring domain registration or subscription. This universal access method allows anyone to interact with decentralized resources using a simple URL format.","sidebar":"tutorialSidebar"},"browser/browser-installation":{"id":"browser/browser-installation","title":"Browser Installation","description":"This guide covers installation options for the WTTP Browser, including pre-built Windows releases and development setup for all platforms.","sidebar":"tutorialSidebar"},"browser/browser-modifications":{"id":"browser/browser-modifications","title":"Browser Modifications","description":"This document details the specific modifications made to the Min Browser to enable WTTP (Web3 Transfer Protocol) support. These changes transform Min into a Web3-native browser capable of accessing decentralized content directly from smart contracts.","sidebar":"tutorialSidebar"},"browser/browser-overview":{"id":"browser/browser-overview","title":"Browser Overview","description":"The WTTP Browser is a specialized fork of the Min Browser that has been enhanced with Web3 Transfer Protocol (WTTP) support, enabling direct access to decentralized websites and applications hosted on Ethereum and other EVM-compatible blockchains.","sidebar":"tutorialSidebar"},"browser/browser-usage":{"id":"browser/browser-usage","title":"Browser Usage","description":"This guide shows you how to use the WTTP Browser to access decentralized websites and applications. Learn about URL formats, explore live examples, and discover the full potential of Web3 browsing.","sidebar":"tutorialSidebar"},"esp/datapoints":{"id":"esp/datapoints","title":"DataPoints","description":"Data points are the fundamental unit of storage in the Ethereum Storage Protocol (ESP). They provide immutable, content-addressed storage with unique identifiers based on the data content itself.","sidebar":"tutorialSidebar"},"esp/esp-overview":{"id":"esp/esp-overview","title":"ESP Overview","description":"The Ethereum Storage Protocol (ESP) is a decentralized storage system built on Ethereum that provides immutable, content-addressed data storage with built-in economic incentives for data publishers.","sidebar":"tutorialSidebar"},"esp/esp-storage":{"id":"esp/esp-storage","title":"ESP Storage","description":"The DataPointStorage contract is the core storage layer of ESP, providing immutable, content-addressed storage for data points. It handles the fundamental operations of storing and retrieving data with cryptographic integrity guarantees.","sidebar":"tutorialSidebar"},"esp/registry":{"id":"esp/registry","title":"Registry","description":"The DataPointRegistry is the economic layer of ESP that manages data point publishing, royalty payments, and re-registration control. It extends the basic storage functionality with economic incentives and publisher management.","sidebar":"tutorialSidebar"},"esp/royalties":{"id":"esp/royalties","title":"Royalties","description":"The ESP royalty system provides economic incentives for data publishers by allowing them to earn royalties when developers re-register the same data on-chain. This creates a self-sustaining economic model that rewards data creators for preventing duplicate uploads and ensures the protocol\'s long-term viability.","sidebar":"tutorialSidebar"},"handler/handler-examples":{"id":"handler/handler-examples","title":"Handler Examples","description":"This guide provides practical examples of using the DIP Handler in various scenarios. These examples demonstrate real-world usage patterns and best practices.","sidebar":"tutorialSidebar"},"handler/handler-installation":{"id":"handler/handler-installation","title":"Handler Installation","description":"This guide covers installing and setting up the DIP Handler for your project. The handler supports both Node.js and browser environments with full TypeScript support and CommonJS compatibility.","sidebar":"tutorialSidebar"},"handler/handler-overview":{"id":"handler/handler-overview","title":"Handler Overview","description":"The DIP (Decentralized Internet Project) Handler is a comprehensive client library that provides a unified interface for accessing decentralized content across multiple protocols. While currently focused on WTTP (Web Three Transfer Protocol), it\'s evolving into a complete DIP handler that supports IPFS, Bitcoin Ordinals, Arweave, ESP, and other decentralized storage solutions.","sidebar":"tutorialSidebar"},"handler/handler-roadmap":{"id":"handler/handler-roadmap","title":"Handler Roadmap","description":"The DIP (Decentralized Internet Project) Handler is evolving from a WTTP-only client into a comprehensive multi-protocol handler for decentralized storage and web protocols. This roadmap outlines our development timeline and future enhancements.","sidebar":"tutorialSidebar"},"intro/philosophy":{"id":"intro/philosophy","title":"Philosophy","description":"The Decentralized Internet Project is built on a foundation of principles that guide our development, community, and vision for the future of the web. These principles shape how we approach technology, collaboration, and the broader ecosystem.","sidebar":"tutorialSidebar"},"intro/roadmap":{"id":"intro/roadmap","title":"Roadmap","description":"The Decentralized Internet Project roadmap outlines our vision for building a more collaborative and decentralized web. This roadmap covers both current development and future expansion plans.","sidebar":"tutorialSidebar"},"intro/what-is-the-dip":{"id":"intro/what-is-the-dip","title":"What is The DIP","description":"The Decentralized Internet Project (DIP) is a comprehensive ecosystem of protocols, tools, and infrastructure designed to create a more collaborative and decentralized web. Built on blockchain technology, DIP enables developers to host websites, applications, and content directly on smart contracts while providing familiar web development experiences.","sidebar":"tutorialSidebar"},"wttp/wttp-deployment":{"id":"wttp/wttp-deployment","title":"WTTP Deployment","description":"This comprehensive guide covers all aspects of deploying WTTP Site contracts, from initial setup to production deployment and ongoing maintenance.","sidebar":"tutorialSidebar"},"wttp/wttp-gateway":{"id":"wttp/wttp-gateway","title":"WTTP Gateway","description":"The WTTP Gateway is a specialized smart contract designed to optimize content delivery from WTTP sites for off-chain applications. It provides a single-call interface that eliminates the need for multiple blockchain transactions when fetching content, especially large files stored across multiple data points.","sidebar":"tutorialSidebar"},"wttp/wttp-methods":{"id":"wttp/wttp-methods","title":"WTTP Methods","description":"WTTP implements a comprehensive set of HTTP-like methods across three main components:","sidebar":"tutorialSidebar"},"wttp/wttp-overview":{"id":"wttp/wttp-overview","title":"WTTP Overview","description":"What is a WTTP Site?","sidebar":"tutorialSidebar"},"wttp/wttp-permissions":{"id":"wttp/wttp-permissions","title":"WTTP Permissions","description":"WTTP Site implements a sophisticated role-based access control (RBAC) system that allows you to fine-tune who can access and modify different parts of your on-chain website. This system provides both security and flexibility for managing your site\'s content and functionality.","sidebar":"tutorialSidebar"},"wttp/wttp-sites":{"id":"wttp/wttp-sites","title":"WTTP Sites","description":"WTTP Site provides comprehensive tools for managing your on-chain websites, from initial deployment to ongoing maintenance and updates. This guide covers all aspects of site lifecycle management.","sidebar":"tutorialSidebar"},"wttp/wttp-storage":{"id":"wttp/wttp-storage","title":"WTTP Storage","description":"The BaseWTTPStorage contract is the core storage layer of the WTTP protocol, providing a sophisticated resource management system built on top of the Ethereum Storage Protocol (ESP). This contract manages file metadata, headers, content organization, and chunked storage for efficient blockchain-based web content.","sidebar":"tutorialSidebar"}}}}')}}]);